package keeper_test

import (
	sdk "github.com/cosmos/cosmos-sdk/types"
	sdkerrors "github.com/cosmos/cosmos-sdk/types/errors"

	"github.com/Pylons-tech/pylons/x/pylons/keeper"
	"github.com/Pylons-tech/pylons/x/pylons/types"
)

func (suite *IntegrationTestSuite) TestMsgServerappleInAppPurchaseGetCoins() {
	k := suite.k
	bk := suite.bankKeeper
	ctx := suite.ctx
	require := suite.Require()

	srv := keeper.NewMsgServerImpl(k)
	wctx := sdk.WrapSDKContext(ctx)

	for _, tc := range []struct {
		desc    string
		request *types.MsgAppleIap
		balance sdk.Coins
		err     error
	}{
		{
			// invalid product id is embedded within the receipt data
			desc: "WrongProductId",
			request: &types.MsgAppleIap{
				Creator:           types.GenTestBech32FromString("sender"),
				ReceiptDataBase64: "",
			},
			err: sdkerrors.Wrap(sdkerrors.ErrInvalidRequest, "invalid product id"),
		},
		{
			// invalid receipt data
			desc: "WrongData",
			request: &types.MsgAppleIap{
				Creator:           types.GenTestBech32FromString("sender"),
				ReceiptDataBase64: "",
			},
			err: sdkerrors.Wrap(sdkerrors.ErrInvalidRequest, "invalid receipt"),
		},
		{
			// valid recipet 1 10 coins
			desc: "Valid Receipt 1",
			request: &types.MsgAppleIap{
				Creator:           types.GenTestBech32FromString("sender"),
				ReceiptDataBase64: "",
				ProductId:         "pylons_10",
				PurchaseId:        "2000000086523782",
			},
			balance: sdk.NewCoins(sdk.NewCoin(types.PylonsCoinDenom, sdk.NewInt(10000000))),
		},
		{
			// valid recipet 2 35 coins
			desc: "Valid Receipt 2",
			request: &types.MsgAppleIap{
				Creator:           types.GenTestBech32FromString("sender"),
				ReceiptDataBase64: "",
				ProductId:         "pylons_35",
				PurchaseId:        "2000000086528808",
			},
			balance: sdk.NewCoins(sdk.NewCoin(types.PylonsCoinDenom, sdk.NewInt(45000000))),
		},
		{
			// valid recipet 3 60 coins
			desc: "Valid Receipt 3",
			request: &types.MsgAppleIap{
				Creator:           types.GenTestBech32FromString("sender"),
				ReceiptDataBase64: "",
				ProductId:         "pylons_60",
				PurchaseId:        "2000000086529484",
			},
			balance: sdk.NewCoins(sdk.NewCoin(types.PylonsCoinDenom, sdk.NewInt(105000000))),
		},
		{
			// invalid product id is embedded within the receipt data
			desc: "Re use receipt",
			request: &types.MsgAppleIap{
				Creator:           types.GenTestBech32FromString("sender"),
				ReceiptDataBase64: "",
				ProductId:         "pylons_60",
				PurchaseId:        "2000000086529484",
			},
			err: sdkerrors.Wrap(types.ErrReceiptAlreadyUsed, "the Apple IAP order ID is already being used"),
		},
	} {
		tc := tc
		suite.Run(tc.desc, func() {
			_, err := srv.AppleIap(wctx, tc.request)
			if tc.err != nil {
				require.ErrorIs(err, tc.err)
			} else {
				require.NoError(err)
				addr, _ := sdk.AccAddressFromBech32(tc.request.Creator)
				balance := bk.SpendableCoins(ctx, addr)
				require.True(balance.IsEqual(tc.balance))
			}
		})
	}
}
