package types

import (
	"testing"

	// sdk "github.com/cosmos/cosmos-sdk/types"
	sdkerrors "github.com/cosmos/cosmos-sdk/types/errors"
	"github.com/stretchr/testify/require"
)

func TestMsgAppleIapValidateBasic(t *testing.T) {
	correctAddr := "cosmos1n67vdlaejpj3uzswr9qapeg76zlkusj5k875ma"
	invalidAddr := "pylo1xn72u3jxlpqx8tfgmjf0xg970q36xensjngsme"
	for _, tc := range []struct {
		desc string
		req  *MsgAppleIap
		err  error
	}{
		{
			desc: "Valid",
			req: &MsgAppleIap{
				Creator:           correctAddr,
				ReceiptDataBase64: "",
				ProductId:         "pylons_10",
				PurchaseId:        "2000000086523782",
			},
			err: nil,
		},
		{
			desc: "Invalid creator address 1",
			req: &MsgAppleIap{
				Creator:           "",
				ReceiptDataBase64: "",
				ProductId:         "pylons_10",
				PurchaseId:        "2000000086523782",
			},
			err: sdkerrors.ErrInvalidAddress,
		},
		{
			desc: "Invalid creator address 2",
			req: &MsgAppleIap{
				Creator:           invalidAddr,
				ReceiptDataBase64: "",
				ProductId:         "pylons_10",
				PurchaseId:        "2000000086523782",
			},
			err: sdkerrors.ErrInvalidAddress,
		},
		{
			desc: "Invalid creator address 3",
			req: &MsgAppleIap{
				Creator:           "test",
				ReceiptDataBase64: "",
				ProductId:         "pylons_10",
				PurchaseId:        "2000000086523782",
			},
			err: sdkerrors.ErrInvalidAddress,
		},
	} {
		tc := tc
		t.Run(tc.desc, func(t *testing.T) {
			err := tc.req.ValidateBasic()
			if err != nil {
				require.ErrorIs(t, err, tc.err)
			} else {
				require.NoError(t, tc.err)
			}
		})
	}
}
